SELECT *
FROM streamers;

-- 初期はclipsが人気上位１０件を有名ストリーマーとする
-- システム的には総選挙をする必要がある
WITH list AS (SELECT broadcaster_id, broadcast_name, SUM(view_count) FROM clips group by broadcast_name, broadcaster_id)
SELECT *
FROM list
ORDER BY sum DESC;

